import axios from "axios";
import React, { useEffect, useState } from "react";
import Container from "react-bootstrap/esm/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useParams } from "react-router-dom";
import Header from "../components/Header";

const Cardetails = () => {
  const params = useParams();
  const { r_id } = params;
  //for getting pid with help of params
  const [cars, setCars] = useState();
  useEffect(() => {
    const fetchData = async () => {
      await axios
        .get(`http://10.0.1.205:8800/api/cars/getcarsbyid/${r_id}`)
        .then((response) => setCars(response.data));
    };
    fetchData();
  }, [r_id]);
  return (
    <div>
      <Header />
      <div className="d-flex flex-column">
          <Container>

          
      <div className="ABody">
        {products?.map((product) => {
          return (
            <Row key={product.p_id}>
              <Col md={6} key={product.p_id}>
                <img
                  className="img-large"
                  src={`/upload/${product.p_image}`}
                  alt={product?.p_name}
                ></img>
              </Col>
              <Col md={3}>
                <ListGroup variant="flush">
                  <ListGroup.Item>
                    <h1>{product?.p_name}</h1>
                  </ListGroup.Item>
                  <ListGroup.Item>Price:${product?.p_price}</ListGroup.Item>
                  <ListGroup.Item>Description:{product?.p_desc}</ListGroup.Item>
                  <ListGroup.Item>
                    <div className="d-grid">
                      <Button
                        variant="primary"
                        onClick={() => handleClick(product.p_id)}
                      >
                        Add to cart
                      </Button>
                    </div>
                  </ListGroup.Item>
                </ListGroup>
              </Col>
            </Row>
          );
        })}
      </div>
      </Container>
          </div>
    </div>
  );
};

export default Cardetails;
