import * as Yup from "yup";

export const signUpSchema = Yup.object({
  username: Yup.string().min(4).max(25).required("Please enter your name"),
  email: Yup.string().email().required("Please enter your email"),
  password: Yup.string().min(6).required("Please enter your password"),
  confirm_password: Yup.string()
    .required()
    .oneOf([Yup.ref("password"), null], "Password must match"),
});

export const sellSchema = Yup.object({
  c_name: Yup.string()
    .min(3)
    .max(25)
    .required("Please enter car company make name"),
  model: Yup.string().required("Please enter model name"),
  date: Yup.date().max(new Date()).required("Please enter date before today "),
  s_price: Yup.number().min(1).positive().required("please enter sell price"),
  miles: Yup.number().positive().required("please enter mile covered by car"),
  car_n: Yup.number().required(
    "please your car registration number(number should be unique)"
  ),
  img: Yup.mixed().required("mimi is required"),
});
