import axios from "axios";
import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import { AuthContext } from "../context/authContext";

const Sellcar = () => {
  const { currentUser } = useContext(AuthContext);

  const [c_name, setC_Name] = useState("");
  const [model, setModel] = useState("");
  const [date, setDate] = useState("");
  const [miles, setMiles] = useState("");
  const [s_price, setS_price] = useState("");
  const [img, setImg] = useState(null);

  const [id, setId] = useState(currentUser.id);

  // const upload = async () => {
  //   try {
  //     const formData = new FormData();
  //     formData.append("img", img);

  //     const res = await axios.post("http://10.0.1.205:8800/uploads", formData);
  //     //console.log(res.data);

  //     return res.data;
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  const upload = async () => {
    try {
      const formData = new FormData();
      //formData.append("img", img);
      //formData.append("img", img);
      Array.from(img).forEach((item) => {
        formData.append("img", item);
      });

      const res = await axios.post("http://10.0.1.205:8800/uploads", formData);
      //console.log(res.data);

      return res.data;
    } catch (error) {
      console.log(error);
    }
  };

  const navigate = useNavigate();

  const handleClick = async (e) => {
    e.preventDefault();
    setId(currentUser.id);
    const imgUrl = await upload();
    console.log(imgUrl);
    try {
      await axios.post("http://10.0.1.205:8800/api/cars/addcar", {
        c_name,
        model,
        date,
        miles,
        s_price,
        imgUrl,
        id, //chnages
      });
      navigate("/home");
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <Header />
      <div className="auth">
        <div className="form">
          <form onSubmit={handleClick}>
            <h1>Add car to sell</h1>
            <div className="mb-3">
              <input
                className="form-control"
                name="c_name"
                type="text"
                value={c_name}
                onChange={(e) => setC_Name(e.target.value)}
                placeholder="company name"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                name="model"
                value={model}
                onChange={(e) => setModel(e.target.value)}
                type="text"
                placeholder="model name"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                name="date"
                value={date}
                type="date"
                onChange={(e) => setDate(e.target.value)}
                data-date-format="dd/mm/yyyy"
                placeholder="enter date"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                name="miles"
                value={miles}
                onChange={(e) => setMiles(e.target.value)}
                type="text"
                placeholder="enter miles"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                name="img"
                type="file"
                multiple
                onChange={(e) => setImg(e.target.files[0])}
                placeholder="enter img"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                name="s_price"
                value={s_price}
                onChange={(e) => setS_price(e.target.value)}
                type="text"
                placeholder="enter selling price"
              />
              <br></br>
              <button type="submit" className="btn btn-primary">
                submit
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Sellcar;
