import axios from "axios";
import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import { AuthContext } from "../context/authContext";
// import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useFormik } from "formik";
import { sellSchema } from "../schemas";

const Sellcar = () => {
  const { currentUser } = useContext(AuthContext);

  const initialValues = {
    c_name: "",
    model: "",
    date: "",
    miles: "",
    s_price: "",
    img: [],
    id: currentUser.id,
  };

  // const [c_name, setC_Name] = useState("");
  // const [model, setModel] = useState("");
  // const [date, setDate] = useState("");
  // const [miles, setMiles] = useState("");
  // const [s_price, setS_price] = useState("");
  // const [img, setImg] = useState([]);

  // const upload = async () => {
  //   try {
  //     const formData = new FormData();
  //     formData.append("img", img);

  //     const res = await axios.post("http://10.0.1.205:8800/uploads", formData);
  //     //console.log(res.data);

  //     return res.data;
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // const upload = async (e) => {
  //   try {
  //     console.log(img);
  //     const formData = new FormData();

  //     Array.from(img).map((item) => {
  //       formData.append("img", item);
  //     });
  //     const res = await axios.post("http://10.0.1.205:8800/uploads", formData);
  //     return res.data;
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  const navigate = useNavigate();

  // const handleClick = async (e) => {
  //   e.preventDefault();
  //   setId(currentUser.id);

  //   const imgUrl = await upload();
  //   console.log(imgUrl);
  //   let arr1 = [];
  //   imgUrl.map((img) => {
  //     arr1.push(img.filename);
  //   });
  //   console.log(arr1);
  //   let arr2 = JSON.stringify(arr1);
  //   console.log(arr2);

  //   try {
  //     console.log("aniket");
  //     await axios.post("http://10.0.1.205:8800/api/cars/addcar", {
  //       c_name,
  //       model,
  //       date,
  //       miles,
  //       s_price,
  //       arr2,
  //       id, //chnages
  //     });
  //     navigate("/home");
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  const {
    values,
    errors,
    touched,
    setFieldValue,
    handleBlur,
    handleChange,
    handleSubmit,
  } = useFormik({
    initialValues,
    validationSchema: sellSchema,
    onSubmit: async (values, action) => {
      const upload = async (e) => {
        try {
          const formData = new FormData();

          Array.from(img).map((item) => {
            formData.append("img", item);
          });

          
          console.log("aniket");
          const res = await axios.post(
            "http://10.0.1.205:8800/uploads",
            formData
          );
          return res.data;
        } catch (error) {
          console.log(error);
        }
      };
      const imgUrl = await upload();
      console.log(imgUrl);
      let arr1 = [];
      imgUrl.map((img) => {
        arr1.push(img.filename);
      });
      console.log(arr1);
      let img = JSON.stringify(arr1);
      console.log(img);

      try {
        console.log("aniket");
        await axios.post("http://10.0.1.205:8800/api/cars/addcar", {
          values,
        });
        navigate("/home");
      } catch (error) {
        console.log(error);
      }

      action.resetForm();
    },
  });

  return (
    <div>
      <Header />
      <div className="auth">
        <div className="form">
          <form onSubmit={handleSubmit}>
            <h1>Add car to sell</h1>
            <div className="mb-3">
              <input
                className="form-control"
                name="c_name"
                required
                type="text"
                value={values.c_name}
                onChange={handleChange}
                onBlur={handleBlur}
                placeholder="company name"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                required
                name="model"
                value={values.model}
                onChange={handleChange}
                onBlur={handleBlur}
                type="text"
                placeholder="model name"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                name="date"
                required
                value={values.date}
                type="date"
                onChange={handleChange}
                onBlur={handleBlur}
                data-date-format="dd/mm/yyyy"
                placeholder="enter date"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                required
                name="miles"
                value={values.miles}
                onChange={handleChange}
                onBlur={handleBlur}
                type="text"
                placeholder="enter miles"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                required
                name="img"
                type="file"
                multiple
                //onChange={(e) => setImg(e.target.files)}
                onBlur={handleBlur}
                onChange={(e) => setFieldValue("img", e.currentTarget.files)}
                placeholder="enter img"
              />
            </div>
            <div className="mb-3">
              <input
                className="form-control"
                required
                name="s_price"
                value={values.s_price}
                onChange={handleChange}
                onBlur={handleBlur}
                type="text"
                placeholder="enter selling price"
              />
              <br></br>
              <button type="submit" className="btn btn-primary">
                submit
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Sellcar;
