import React, { useContext } from "react";
import { Link } from "react-router-dom";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { LinkContainer } from "react-router-bootstrap";
import Container from "react-bootstrap/Container";
import { AuthContext } from "../context/authContext";

const Header = () => {
  const { currentUser, logout } = useContext(AuthContext);
  return (
    <div>
      <header className="mb-4">
        <Navbar bg="dark" variant="dark">
          <Container>
            <LinkContainer to="/">
              <Navbar.Brand>E- mart</Navbar.Brand>
            </LinkContainer>

            <Nav className="me-auto">
              <Link to="/mobile" className="nav-link">
                Mobile
              </Link>
              <Link to="/laptop" className="nav-link">
                Laptop
              </Link>
              <Link to="/accessories" className="nav-link">
                Accessories
              </Link>
            </Nav>
            <Nav className="">
              <i className="fa fa-shopping-cart" aria-hidden="true">
                cart
              </i>
              <Link to="/mycart" className="nav-link">
                cart
                <i className="fa fa-cart-plus fa-6" aria-hidden="true"></i>
              </Link>
              <div className="UserName">
                <span></span>
                {currentClient && (
                  <span
                    className="logoutUser"
                    style={{
                      padding: "8px",
                      fontSize: "18px",
                      marginTop: "2px",
                    }}
                    onClick={logout}
                  >
                    Logout {currentUser?.username}
                  </span>
                )}
              </div>
            </Nav>
          </Container>
        </Navbar>
      </header>
    </div>
  );
};

export default Header;
