import React, { useContext } from "react";
import { Link } from "react-router-dom";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import { LinkContainer } from "react-router-bootstrap";
import Container from "react-bootstrap/Container";
import { AuthContext } from "../context/authContext";

const Header = () => {
  const { currentUser, logout } = useContext(AuthContext);
  return (
    <div>
      <header className="App-header">
            <Navbar bg="dark" variant="dark" >

              <Container>
               <LinkContainer to="/vendor/Home">
                  <Navbar.Brand>E-mart</Navbar.Brand>
                </LinkContainer>

                {currentUser && <LinkContainer to="/vendor/add">
                  <Navbar.Brand>Add Product</Navbar.Brand>
                </LinkContainer>}
              </Container>
              
              <div className="UserName">
                <span></span>
                {currentUser && <span className="logoutUser" onClick={logout}>Logout {currentUser?.v_name}</span>}
              </div>
            </Navbar>
          </header>
    </div>
  );
};

export default Header;
